package main

import (
	"fmt"
	"testing"
)

func TestN005LongestPalindromic(t *testing.T) {
	var n005 N005LongestPalindromic
	fmt.Print("N005LongestPalindromic:\t", n005.longestPalindrome("ccc"), " ")
	fmt.Print(n005.longestPalindrome("abcdabcbadcbagf"), " ")
	fmt.Print(n005.longestPalindrome(""), " ")
	fmt.Print(n005.longestPalindrome(""), " ")
	fmt.Print(n005.longestPalindrome(""), " ")
	fmt.Println()
}
